package dayOne;

public class dayThreepractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// calling calculator function
		calculator(7,3);
		calculator(17,8);
		calculator(9,2);
		calculator(13,5);

	}
	public static void calculator(int x, int y) {
		System.out.println(x+y);
		System.out.println(x-y);
		System.out.println(x*y);
		System.out.println(x/y);
		System.out.println(x%y);
	}

}
